import React from 'react';
import { MDBContainer, MDBCard, MDBCardBody } from 'mdb-react-ui-kit';
import NavBar from '../components/NavBar.tsx';
import { useAuth } from '../context/AuthContext.tsx';
import Footer from '../components/Footer.tsx';
import backgroundImage from '../assets/background_img.jpg'; 


const UserPage: React.FC = () => {
  const { user } = useAuth();

  return (
    <div style={{ backgroundColor: '#f8f9fa', minHeight: '100vh' }}>
      <NavBar username={user?.user_name || ''} />

      <MDBContainer className="d-flex justify-content-center align-items-center" style={{ 
        minHeight: '80vh',
        backgroundImage: `url(${backgroundImage})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
        }}
        >
        <MDBCard className="w-100 shadow-4" style=
        {{  
          maxWidth: '700px',
          borderRadius: '12px',
          backgroundColor: 'rgba(255, 255, 255, 0.3)', 
          backdropFilter: 'blur(8px)',                 
          WebkitBackdropFilter: 'blur(8px)',          
          border: '1px solid rgba(255, 255, 255, 0.3)'  
        }}
        >
          <MDBCardBody className="text-center">
          <h1 className="mb-4">Welcome, {user?.user_name || 'Guest'}!</h1>
            <p className="lead">Use the menu to submit or search for biases.</p>
          </MDBCardBody>
        </MDBCard>
      </MDBContainer>
      <Footer />
    </div>
  );
};

export default UserPage;
