import React, { useState, useEffect } from 'react';
import {
  MDBContainer,
  MDBInput,
  MDBDropdown,
  MDBDropdownMenu,
  MDBDropdownToggle,
  MDBDropdownItem,
  MDBPagination,
  MDBPaginationItem,
  MDBPaginationLink,
} from 'mdb-react-ui-kit';
import NavBar from './NavBar.tsx';
import { useAuth } from '../context/AuthContext.tsx';
import BiasTable, { Bias } from './BiasTable.tsx';
import '../styles/SearchBias.css';

const SearchBias: React.FC = () => {
  const { user } = useAuth();
  const [searchTerm, setSearchTerm] = useState('');
  const [biases, setBiases] = useState<Bias[]>([]);
  const [severityFilter, setSeverityFilter] = useState<string>('');
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 15;

  useEffect(() => {
    const fetchBiases = async () => {
      try {
        const res = await fetch(
          `http://localhost:4000/api/biases?search=${encodeURIComponent(
            searchTerm
          )}&severity=${encodeURIComponent(severityFilter)}`
        );
        const data = await res.json();
        if (data.success) {
          setBiases(data.biases);
          setCurrentPage(1);
        }
      } catch (error) {
        console.error('Error fetching biases:', error);
      }
    };

    fetchBiases();
  }, [searchTerm, severityFilter]);

  const totalPages = Math.ceil(biases.length / itemsPerPage);
  const startIndex = (currentPage - 1) * itemsPerPage;
  const currentBiases = biases.slice(startIndex, startIndex + itemsPerPage);

  return (
    <div style={{ backgroundColor: '#f8f9fa', minHeight: '100vh' }}>
      <NavBar username={user?.user_name || ''} />
      <MDBContainer className="py-4">
        <div className="d-flex justify-content-between align-items-center mb-3">
          <MDBInput
            label="Search Biases"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            style={{ maxWidth: '300px' }}
            className="search-box"
          />

          <MDBDropdown group>
            <MDBDropdownToggle color="secondary">
              Filter: Severity
            </MDBDropdownToggle>
            <MDBDropdownMenu>
              <MDBDropdownItem link onClick={() => setSeverityFilter('')}>
                All
              </MDBDropdownItem>
              <MDBDropdownItem link onClick={() => setSeverityFilter('High')}>
                High
              </MDBDropdownItem>
              <MDBDropdownItem link onClick={() => setSeverityFilter('Critical')}>
                Critical
              </MDBDropdownItem>
            </MDBDropdownMenu>
          </MDBDropdown>
        </div>

        <BiasTable biases={currentBiases} />

        <MDBPagination className="mb-0 justify-content-center mt-4">
          {[...Array(totalPages)].map((_, i) => (
            <MDBPaginationItem key={i} active={i + 1 === currentPage}>
              <MDBPaginationLink href="#" onClick={() => setCurrentPage(i + 1)}>
                {i + 1}
              </MDBPaginationLink>
            </MDBPaginationItem>
          ))}
        </MDBPagination>
      </MDBContainer>
    </div>
  );
};

export default SearchBias;
