import React, { useState } from 'react';
import {
  MDBContainer,
  MDBCard,
  MDBCardBody,
  MDBInput,
  MDBTextArea,
  MDBBtn
} from 'mdb-react-ui-kit';

const SubmitBias: React.FC = () => {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    category: '',
  });

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      const res = await fetch('http://localhost:5000/api/biases', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });
      if (!res.ok) throw new Error('Submission failed');
      alert('Bias submitted!');
    } catch (err) {
      console.error(err);
      alert('Failed to submit bias.');
    }
  };

  return (
    <div style={{ backgroundColor: '#f8f9fa', minHeight: '100vh' }}>
      <MDBContainer className="d-flex justify-content-center align-items-center" style={{ minHeight: '80vh' }}>
        <MDBCard className="w-100 shadow-4" style={{ maxWidth: '700px', borderRadius: '12px' }}>
          <MDBCardBody>
            <h4 className="mb-4 text-center">Submit a New Bias</h4>
            <form onSubmit={handleSubmit}>
              <MDBInput
                className='mb-4'
                label='Title'
                name='title'
                value={formData.title}
                onChange={handleChange}
                required
              />
              <MDBTextArea
                className='mb-4'
                label='Description'
                name='description'
                rows={4}
                value={formData.description}
                onChange={handleChange}
                required
              />
              <MDBInput
                className='mb-4'
                label='Category'
                name='category'
                value={formData.category}
                onChange={handleChange}
                required
              />
              <MDBBtn type='submit' block color='success'>Submit</MDBBtn>
            </form>
          </MDBCardBody>
        </MDBCard>
      </MDBContainer>
    </div>
  );
};

export default SubmitBias;
