import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useNavigate } from 'react-router-dom';
import './App.css';

const NavBar: React.FC<{ username: string }> = ({ username }) => {
  const navigate = useNavigate();

  const isAdmin = username.toLowerCase() === 'admin';

  return (
    <nav className="navbar">
      <div className="logo">Bias System</div>
      <div className="nav-links">
        <button onClick={() => navigate('/submit')}>Submit Bias</button>
        <button onClick={() => navigate('/search')}>Search Bias</button>
        {isAdmin && <button onClick={() => navigate('/editor')}>Editor</button>}
      </div>
    </nav>
  );
};

const UserPage: React.FC<{ username: string }> = ({ username }) => {
  return (
    <div>
      <NavBar username={username} />
      <div className="content">
        <h1>Welcome, {username}!</h1>
      </div>
    </div>
  );
};

const SubmitBias: React.FC = () => {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    category: ''
  });

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    console.log('Submitting Bias:', formData);
    // Add API logic here
  };

  return (
    <div className="form-container">
      <h2>Submit New Bias</h2>
      <form onSubmit={handleSubmit}>
        <input name="title" placeholder="Title" value={formData.title} onChange={handleChange} required />
        <textarea name="description" placeholder="Description" value={formData.description} onChange={handleChange} required />
        <input name="category" placeholder="Category" value={formData.category} onChange={handleChange} required />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

const App: React.FC = () => {
  const [username] = useState('JohnDoe'); // Replace this with actual login logic

  return (
    <Router>
      <Routes>
        <Route path="/" element={<UserPage username={username} />} />
        <Route path="/submit" element={<SubmitBias />} />
        <Route path="/search" element={<div className="content">Search Bias Page</div>} />
        <Route path="/editor" element={<div className="content">Editor Page</div>} />
      </Routes>
    </Router>
  );
};

export default App;
